--algorithm MetaPlanReview
variables metaPlan, workstreams, deps, unknowns, ok in {FALSE, TRUE};
begin
  Init:
    metaPlan.title := "<set in plan>"; (* Title provided by operator *)
    workstreams := {};
    deps := {};
    unknowns := {};
    ok := TRUE;

  DefineWorkstreams:
    workstreams := {"inventory", "dependencies", "unknowns", "acceptance"}; (* Ensure core tracks are present *)

  CaptureDependencies:
    deps := CollectDependencies(metaPlan); (* List registry updates, approvals, inputs *)
    if Cardinality(deps) = 0 then ok := FALSE; end if;

  CaptureUnknowns:
    unknowns := CollectQuestions(metaPlan); (* Record blocking questions for operator *)
    if Cardinality(unknowns) = 0 then ok := FALSE; end if;

  CheckAcceptance:
    if NOT Contains(metaPlan, workstreams) then ok := FALSE; end if;

  Result:
    assert ok = TRUE; (* meta-plan ready for operator review *)
end algorithm
