  Init:
    reg := LoadJSON('<path-to-registry>'); (* Load registry file *)
    entries := reg.registry.allowed_templates;
    ok := TRUE;

  CheckFields:
    if reg.name = Nil OR reg.version = Nil OR reg.category = Nil then ok := FALSE; end if;
    if Cardinality(entries) = 0 then ok := FALSE; end if;

