--algorithm RegistryQuickIngest
variables reg, entries, ok in {FALSE, TRUE};
begin
  Init:
    reg := LoadJSON(".pipeline-x/templates/template-registry.json"); (* short registry *)
    entries := reg.registry.allowed_templates;
    ok := TRUE;

  Validate:
    if Cardinality(entries) = 0 then ok := FALSE; end if;
    if NOT Unique([e IN entries |-> e.id]) then ok := FALSE; end if;
    if NOT Unique([e IN entries |-> e.path]) then ok := FALSE; end if;

  Result:
    assert ok = TRUE; (* short registry ready for ingestion *)
end algorithm
